$(document).ready(function(){$("#telefone").mask("(00) 00000-0000"),$("form").validate({rules:{nome:{required:!0},telefone:{required:!1},email:{required:!0,email:!0}},messages:{nome:"Por favor, insira o seu nome completo",telefone:"Por favor, insira o número do seu telefone",email:"Por favor, insira o seu e-mail"},submitHandler:function(e){$(e).valid()&&(alert("Formulário enviado com sucesso!"),e.reset())},invalidHandler:function(e,o){o=o.numberOfInvalids();o&&alert(`Existem ${o} campos incompletos`)}})});